tag-index.map:
function (doc, meta) {
  if (meta.type == 'json' && meta.id.match('^dashboards/.*')) {
    if (doc.dashboard.tags) {
      for (i = 0; i < doc.dashboard.tags.length; i++) {
	emit(doc.dashboard.tags[i], meta.id);
      }
    }
  }
}

title-index.map:
function (doc, meta) {
  if (meta.type == 'json' && meta.id.match('^dashboards/.*')) {
    if (doc.dashboard.title) {
      emit(doc.dashboard.title, meta.id);
    }
  }
}
